@import 'theme';
@import 'components';

body {
	background-color: $a-b;
	-webkit-font-smoothing: antialiased;
	margin: 0;
	user-select: none;
}

.header-nav {
	@include header-nav;
}
.main-wrapper {
	position: relative;
	margin: 0 auto;
	width: 80vw;
	height: 100vh;
}

.dud {
	color: $f-l;
}
.content-wrapper {
	padding-top: 10vh;

	.hero-wrapper {
		.heading {
			@include heading;
		}
		.subheading {
			@include misc-text;
			padding-top: 2vh;
			opacity: 0;
			width: 50%;
			animation-name: show-objects;
			animation-duration: 0.3s;
			animation-delay: 1s;
			animation-timing-function: cubic-bezier(0.59, 0, 0.42, 0.99);
			animation-fill-mode: forwards;
		}
	}
	#projects {
		padding-top: 10vh;
		opacity: 0;
		animation-name: show-objects;
		animation-duration: 0.3s;
		animation-delay: 1.4s;
		animation-timing-function: cubic-bezier(0.59, 0, 0.42, 0.99);
		animation-fill-mode: forwards;

		.heading {
			@include sub-heading;
			color: $f-h;
		}

		.subheading {
			@include sub-heading;
			color: $t-f;
			padding-top: 0.5vh;
		}
	}
}

#arrow {
	opacity: 0;
	background-repeat: no-repeat;
	left: 0;
	bottom: 0;
	margin-top: 16px;
}

.project-list a:hover {
	cursor: pointer;
}

.project-list a:hover > #arrow {
	animation-name: move-arrow;
	animation-duration: 0.4s;
	animation-timing-function: cubic-bezier(0.59, 0, 0.42, 0.99);
	animation-fill-mode: forwards;
}

.project-list a:visited {
	text-decoration: none;
}

.project-list a:link {
	text-decoration: none;
}

.flex-wrapper {
	display: flex;
	flex-wrap: wrap;
}

#projects .flex-wrapper > div {
	margin-right: 6vw;
	flex: 1 0 21%;
	max-width: 33%;
	flex-grow: 0;
	margin-bottom: 4vh;
}

/*

* Footer

*/
.footer-nav {
	@include footer-nav;
	width: 80%;
	opacity: 0;
	animation: move-footer;
	animation-fill-mode: forwards !important;
	animation-duration: 0.6s;
	animation-delay: 1.8s;

	.nav-text {
		margin-left: 3vw;
	}
}
/*

* All the animations

*/

@keyframes move-arrow {
	from {
		opacity: 0;
		transform: translateX(-4px);
	}
	to {
		opacity: 1;
		transform: translateX(0px);
	}
}

@keyframes show-context {
	from {
	}
	to {
		opacity: 0.3;
	}
}

@keyframes show-objects {
	from {
		opacity: 0;
		transform: translatey(1vh);
	}
	to {
		opacity: 1;
		transform: translatey(0vh);
	}
}

@keyframes move-footer {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
		display: block;
	}
}

